sys_script_include.script:
var NeedItUtils = Class.create();
NeedItUtils.prototype = {
	initialize: function() {
	},

	isDatePast: function(strDate){
		// Current time (and day part)
		var rightnow = new GlideDateTime();
		var dayRightnow = rightnow.getDate();

		// GlideDateTime object for the When needed date (and day part)
		var whenNeeded = new GlideDateTime(strDate);
		var dayWhenNeeded = whenNeeded.getDate();

		//gs.info("#### SI: rightnow: {0} - whenNeeded: {1} - whenNeeded.before(rightnow): {2}", rightnow.toString(), whenNeeded.toString(), whenNeeded.before(rightnow));
		//gs.info("#### SI: dayRightnow: {0} - dayWhenNeeded: {1}", dayRightnow.toString(), dayWhenNeeded.toString());

		if(whenNeeded.before(rightnow) || (dayRightnow == dayWhenNeeded))
			return true;
		else
			return false;

	},

	type: 'NeedItUtils'
};
